import React, { Component } from "react";
import ReactDOM from "react-dom";
const $ = require("jquery");
$.DataTable = require("datatables.net");

const columns = [];
const COLUMN_MAPPING = [
  {title: 'LD ID', data: 'id'},
  {title: 'STK'  , data: 'item_attributes.diamond#stock_number.trans'},
  {title: 'CERT' , data: 'item_attributes.diamond#cert_number.trans'},
  {title: 'AGING' , data: 'aging_group'},
  {title: 'SHP'  , data: 'item_attributes.diamond#shape.trans'},
  {title: 'COL'  , data: 'item_attributes.diamond#color.trans'},
  {title: 'CLAR' , data: 'item_attributes.diamond#clarity.trans'},
  {title: 'CTS'  , data: 'item_attributes.gem#carats.trans'},
  {title: 'MILKY', data: 'item_attributes.diamond#milky.trans'},
  {title: 'SHADE', data: 'item_attributes.diamond#shade.trans'},
  {title: 'MAKE' , data: 'item_attributes.diamond#make.trans'},
  {title: 'FLOUR', data: 'item_attributes.diamond#fluor.trans'},
  // {title: 'BIC'  , data: 'item_attributes.diamond#black_in_center.trans'},
  // {title: 'BIS'  , data: 'item_attributes.diamond#black_in_side.trans'},
  // {title: 'WIC'  , data: 'item_attributes.diamond#white_in_center.trans'},
  // {title: 'WIS'  , data: 'item_attributes.diamond#white_in_side.trans'},
  // {title: 'LAB'  , data: 'item_attributes.diamond#cert.trans'},
  {title: 'RAP %', data: 'item_attributes.diamond#rap_percent.trans'},
  {title: '$/CT' , data: 'item_attributes.gem#price_per_carat.trans'},
  {title: 'Total', data: 'sell_offer_cents', render: function ( data, type, row ) {return '$' + (data/100)}}]

class MyDataTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: this.props.data,
      columns: COLUMN_MAPPING
    };
  }

  componentDidMount() {
    this.$el = $(this.el);
    this.$el.DataTable({
      responsive: true,
      displaylength: 100,
      pagingType: 'full_numbers',
      data: this.state.data,
      columns: this.state.columns
    });
  }

  reloadTableData = (data) => {
    const table = $('.data-table-wrapper').find('table').DataTable();
    table.clear();
    table.rows.add(data);
    table.draw();
  }

  shouldComponentUpdate(nextProps, nextState){
    if (nextProps.data.length !== this.state.data.length) {
      this.reloadTableData(nextProps.data);
    }
    return false;
  }

  render() {
    return (
      <div>
        <table className="table table-borderless display" id="dataTable" ref={(el) => (this.el = el)} />
      </div>
    );
  }
}
export default MyDataTable;
