import React, {Component, createRef} from 'react'
import './report.css'
import StaticReport from './StaticReport';
import TurnReport from './TurnReport';
import Multiselect from 'multiselect-react-dropdown';
import Button from 'react-bootstrap/Button';
import { CSVLink, CSVDownload } from "react-csv";
import { BiDownload } from "react-icons/bi";
import { ColorRing } from  'react-loader-spinner'
import Form from 'react-bootstrap/Form';
import Cookies from 'js-cookie';
import * as Bootstrap from 'react-bootstrap';
import Moment from 'moment';

import "react-datepicker/dist/react-datepicker.css";

const SUMMARY_DATA_PROPERTIES = {'lab':'diamond#cert','shape': 'diamond#shape','color': 'diamond#color','clarity': 'diamond#clarity','carats': 'gem#carats','milky': 'diamond#milky','shade': 'diamond#shade','make': 'diamond#make','fluor': 'diamond#fluor','bic': 'diamond#black_in_center','bis': 'diamond#black_in_side','wis': 'diamond#white_in_side','wic': 'diamond#white_in_center'}
const ICON_HASH_FOR_SUMMERY = {'Total Price': ['bi-card-checklist', 'sales-card'], 'Average Price': ['bi-currency-dollar', 'revenue-card'], 'Count': ['bi-people' , 'customers-card']}
const REPORT_HASH = {'inventory_report': [true, 'Inventory Report'], 'sale_report': [false, 'Sale Report'], 'aging_report': [false,'Aging Report']}

class InventoryReport extends Component {
  constructor(props) {
    super(props);

    const inv_date = Moment(new Date()).format('yyyy-MM-DD')
    this.initialState = {
      itemsData: {},
      options: {},
      reportSummary: {'Total Price': 0, 'Average Price': 0, 'Count': 0},
      DataisLoaded: false,
      BreakdownDataLoaded: false,
      showAlert: false,
      filterHash: {
        'size'                    : 1,
        'end_date'                : '',
        'start_date'              : '',
        'inventory_date'          : inv_date,
        'show_mine'               : true,
        'show_others'             : true,
        'seller_account_id'       : [],
        'get_filtered_data'       : true,
        'item_filter'             : {'discrete': {}},
        'get_only_matching_count' : true,
        'change_breakdown'        : false,
        'get_turn'                : false,
      },
      change_breakdown: false,
      inventory_date: inv_date,
      get_turn: false

    }
    this.initialItemData = createRef(0)
    this.initialRankData = createRef(0)
    this.can_change_breakdown = false
    this.can_get_turn = false
    this.new_inventory_date = inv_date

    this.state = {...this.initialState };

    this.calculateData = this.calculateData.bind(this)
    this.onSelect = this.onSelect.bind(this)
    this.generateOptions = this.generateOptions.bind(this)
    this.captureUrls = this.captureUrls.bind(this)
    this.serialize = this.serialize.bind(this)
    this.getBreakdownData = this.getBreakdownData.bind(this)
    this.handleBreakdownClick = this.handleBreakdownClick.bind(this)
    this.handleTurnClick = this.handleTurnClick.bind(this)
    this.handleTurnDateClick = this.handleTurnDateClick.bind(this)
    this.requestForReports = this.requestForReports.bind(this)
    this.handleCheckClick = this.handleCheckClick.bind(this)
  }

  componentDidMount() {
    this.getBreakdownData();
    this.calculateData();
  }

  requestForReports(){
    // Api call for the request 
    // default msg and hide button
  }

  handleCheckClick(event){
    console.log(event)
    console.log(event.target.name)
  }


  getBreakdownData() {
    let accessToken = Cookies.get('access_token')
    this.captureUrls()
    let queryParams = this.state.filterHash
    fetch(process.env.REACT_APP_API_URL + "/inventory_report", {method: 'POST', headers: {'Authorization': 'Bearer '+ accessToken, 'Content-Type': 'application/json'}, body: JSON.stringify(queryParams)})
      .then(response => response.json())
      .then((response) => {
        this.generateOptions(response)
        if(Object.values(response).every(element => element.length == 0))
          this.setState({showAlert: true});
        this.setState({
          itemsData: response,
          DataisLoaded: true,
          BreakdownDataLoaded: true,
          });
        if (queryParams.get_turn){
          if (!queryParams.change_breakdown && !this.initialRankData.current){
            this.initialRankData.current = response;
          }
        }
        else{
          if (!queryParams.change_breakdown && !this.initialItemData.current){
            this.initialItemData.current = response;
          }
        }
      })
    }

  handleBreakdownClick(event){
    this.can_change_breakdown = event.target.checked
    this.setState({change_breakdown: event.target.checked})
  }
  handleTurnClick(event){
    this.setState({get_turn: event.target.checked})
  }
  handleTurnDateClick(event){
    this.setState({inventory_date: event.target.value})
  }

  captureUrls(){
    const queryParams = new URLSearchParams(window.location.search);
    let sellerIds, startDate, endDate, changeBreakdown, inventoryDate, getTurn
    let filterHash  = this.state.filterHash
    var sellerIdString = queryParams.get('sellerIds')
    if (sellerIdString){
      sellerIds = sellerIdString.split(',');
      filterHash['seller_account_id'] = sellerIds
    }
    this.setState({
      filterHash: filterHash
    })
  }

  serialize(obj, prefix) {
    var str = [],
    p,m;
    for (p in obj) {
      if (obj.hasOwnProperty(p)) {
        if (!isNaN(p)){
          m=''
        }else{
          m=p
        }
        var k = prefix ? prefix + "["+m+"]" : p,
          v = obj[p];
        str.push((v !== null && typeof v === "object") ?
          this.serialize(v, k) :
          encodeURIComponent(k) + "=" + encodeURIComponent(v));
      }
    }
    return str.join("&");
  }

  generateOptions(response){
    Object.entries(response).map(([key, options]) => {
      options.map(data=>{
        data['className'] = key
      })
    })
    this.setState({
      options: response
    })
  }

  calculateData(event){
    let accessToken = Cookies.get('access_token')
    this.setState({BreakdownDataLoaded: false})
    let queryParams = this.serialize(this.state.filterHash)
    fetch(process.env.REACT_APP_API_URL + "/items?"+ queryParams, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken }})
      .then(response => response.json())
      .then((response) => {
        let reportSummary = this.state.reportSummary
        reportSummary['Count'] = response.meta.total
        reportSummary['Total Price'] = response.meta.aggregations.total_sum.slice(0,-3)
        reportSummary['Average Price'] = response.meta.aggregations.average.slice(0,-3)
        this.setState({
          reportSummary: reportSummary
        });
      })

    // Calculate BreakDown
    let filterHash  = this.state.filterHash
    let getBreakdown = false

    if(this.state.get_turn){
      if (this.state.inventory_date === '')
      {
        alert('Please select inventory Date')
        return(0)
      }
      else{
        if(this.can_change_breakdown){
          filterHash.change_breakdown = true
          getBreakdown = true
        }
        else{
          if(this.initialRankData.current){
            this.setState({itemsData: this.initialRankData.current, BreakdownDataLoaded: true})
            getBreakdown = false
          }
          else{
            filterHash = this.initialState.filterHash
            getBreakdown = true
          }
          filterHash.change_breakdown = false
        }
        filterHash.inventory_date = this.new_inventory_date
        filterHash.get_turn = true
      }
    }
    else{
      filterHash.get_turn = false
      filterHash.inventory_date = ''
      if(this.can_change_breakdown){
        filterHash.change_breakdown = true
        getBreakdown = true
      }
      else{
        if(this.initialItemData.current){
          this.setState({itemsData: this.initialItemData.current, BreakdownDataLoaded: true})
          getBreakdown = false
        }
      }
    }
    this.setState({filterHash: filterHash})

    if (getBreakdown)
      this.getBreakdownData()
  }


  onSelect(selectedList, removedItem) {
    var filterValues = []
    var attrName = null
    if (selectedList.length == 0 ){
      attrName = SUMMARY_DATA_PROPERTIES[removedItem.className]
    }  
    Object.entries(selectedList).map(([key, option]) => {
      attrName = SUMMARY_DATA_PROPERTIES[option.className]
      if (attrName) {
        if (attrName === "gem#carats"){
          let carat_range = option.key.split('-')
          filterValues.push({'to':carat_range[0], 'from':carat_range[1]})
        }
        else{
          filterValues.push(option.key)
        }
      }
    })
    let filterHash = this.state.filterHash
    filterHash['item_filter']['discrete'][attrName] = filterValues.flat()
    this.setState({
      filterHash: filterHash
    })
  }

  render(){
    const { DataisLoaded, items } = this.state;
    const getTurn  = (this.state.filterHash.get_turn)
    const showInventoryDateInput = this.state.get_turn;

    let noRecords = Object.values(this.state.itemsData).every(element => element.length == 0)
    let showAlert = noRecords && this.state.showAlert
    if (!DataisLoaded)
      return(
        <div className='row'>
          <div className="align-items-center justify-content-center">
            <h3 className=" text-center"> Fetching reports for you.... </h3>
          </div>
          <ColorRing
            visible={true}
            height="60"
            width="60"
            ariaLabel="blocks-loading"
            wrapperStyle={{}}
            wrapperClass="blocks-wrapper"
            colors={['#00319f', '#2e56b1', '#5c7bc2', '#8aa1d3', '#b2c1e2']}
          />
        </div>)

    return(
      <div className="row justify-content-start border-secondary">
        {showAlert ? 
          <div className="alert alert-warning alert-dismissible fade show text-center" role="alert">
            <i className="bi bi-exclamation-triangle me-1"></i>
              Oops, we couldn’t find any data. Please try with different some filters or params.
            <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
          </div>: null}
        <br></br>
        <div className='filters card multiselect-dropdown'>  
          <div className="text-left">
            <div className="row form-check form-switch multiselect-dropdown">
              <div className='form-check-inline col-sm-2'>
                <input className="form-check-input" type="checkbox" id="breakdownSwitch" name='change_breakdown' value={this.state.filterHash.change_breakdown} onChange={this.handleBreakdownClick}/>
                <label className="form-check-label" htmlFor="breakdownSwitch">Change BreakDown</label>
              </div>
              <div className='form-check-inline col-sm-2'>
                <input className="form-check-input" type="checkbox" id="turnSwitch" name='get_turn' value={this.state.filterHash.get_turn} onChange={this.handleTurnClick}/>
                <label className="form-check-label" htmlFor="turnSwitch">Get Turn Data</label>
              </div>
            </div>
            {showInventoryDateInput ? 
              <div className="row">
                <div className='col-md-2'>
                  <label htmlFor="inputDate" className="col-form-label">Select Inventory Date:</label>
                </div>
                <div className="col-md-3">
                  <input type="date" className="form-control" value={this.state.inventory_date} dateformat="dd/MM/yyyy" onChange={this.handleTurnDateClick}/>
                </div>
              </div> : null
            }
          </div>
          {noRecords ? null : 
          <div className="row mb-3 ">
            {Object.entries(this.state.options).map(([key, options]) => {
              return (
                <div className="col-sm-3 multiselect-dropdown">
                  <Multiselect
                    style={{backgroundColor:'#4154f1'}}
                    optionLabel={"Select "+key}
                    onSelect={this.onSelect}
                    onRemove={this.onSelect}
                    className={key}
                    options={options}
                    displayValue='name'
                    placeholder={"Select "+key}
                    />
                </div>
              )
            })}
          </div>}
        </div>
        <div className='col-lg-12'>  
          <div className="row">
            <div className='col-xxl-2 col-md-6'>
              <button type="button" className="btn btn-primary" onClick={this.calculateData}><i className=" bi bi-sliders me-1"></i> Apply Filters</button>
            </div>
            <div className='col-xxl-2 col-md-6'>
              <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#verticalycentered"><i className=" bi bi-download me-1"></i> Download Report</button>
              <div className="modal fade" id="verticalycentered" tabIndex="-1">
                <div className="modal-dialog modal-dialog-centered">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">Reports to Download</h5>
                      <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                      <div className="row mb-3">
                        <div className="col-sm-10">
                          {Object.entries(REPORT_HASH).map(([key, value_n_name]) => {
                            return (<div className="form-check">
                              <input className="form-check-input" type="checkbox" id={key} name={key} defaultChecked={value_n_name[0]} onChange={this.handleCheckClick}/>
                              <label className="form-check-label" for="gridCheck1">{value_n_name[1]}</label>
                            </div> 
                          )})}
                        </div>
                      </div>

                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                      <button type="button" className="btn btn-primary" onClick={this.requestForReports}>Save changes</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <hr></hr>
        </div>

        {noRecords ? null :
        <div className="col-lg-12 dashboard">
          <div className="row">
            {Object.entries(this.state.reportSummary).map(([key, value]) => {
            return(
              <div className="col-xxl-4 col-md-6">
                <div className={"card info-card "+ ICON_HASH_FOR_SUMMERY[key][1]}>

                  <div className="filter">
                    <a className="icon" data-bs-toggle="dropdown"><i className="bi bi-three-dots"></i></a>
                    <ul className="dropdown-menu dropdown-menu-end dropdown-menu-arrow">
                      <li className="dropdown-header text-start"><h6>Options</h6></li>
                      <li><a className="dropdown-item" href="#">View Stones</a></li>
                    </ul>
                  </div>

                  <div className="card-body">
                    <h5 className="card-title">{key} <span>| Inventory</span></h5>

                    <div className="d-flex align-items-center" data-bs-toggle="modal" data-bs-target="#fullscreenModal">
                      <div className="card-icon rounded-circle d-flex align-items-center justify-content-center">
                        <i className={"bi " + ICON_HASH_FOR_SUMMERY[key][0]}></i>
                      </div>
                      <div className="ps-3">
                        <h6>{value}</h6>
                      </div>
                    </div>
                    <div class="modal fade show" id="fullscreenModal" tabindex="-1">
                      <div class="modal-dialog modal-fullscreen">
                        <div class="modal-content">
                          <div class="modal-header">
                            <h5 class="modal-title">Item Data</h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                          </div>
                          <div class="modal-body">
                            Non omnis incidunt qui sed occaecati magni asperiores est mollitia. Soluta at et reprehenderit. Placeat autem numquam et fuga numquam. Tempora in facere consequatur sit dolor ipsum. Consequatur nemo amet incidunt est facilis. Dolorem neque recusandae quo sit molestias sint dignissimos.
                          </div>
                          <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )})}
          </div>
          <hr></hr>
        </div>}
        {noRecords ? null :
        <div className="pagetitle text-center">
          <h1>BreakDown</h1>
          <br></br>
        </div>}
        {noRecords ? null : <div>{getTurn ? (<TurnReport items={this.state.itemsData} DataisLoaded={this.state.BreakdownDataLoaded}/>) : (<StaticReport items={this.state.itemsData} DataisLoaded={this.state.BreakdownDataLoaded}/>)}</div>}
      </div>
      )
  }
}
export default InventoryReport
