    const { DataisLoaded, items } = this.state;
    const getTurn  = (this.state.filterHash.get_turn)
    const showInventoryDateInput = this.can_get_turn;
    let noRecords = Object.values(this.state.itemsData).every(element => element.length == 0)
    let showAlert = noRecords && this.state.showAlert

    if (!DataisLoaded)
      return(
        <div className='container_row'>
          <div>
            <h1 className="mb-3"> Fetching reports for you.... </h1>
          </div>
          <div className='loader'>
            <ColorRing
              visible={true}
              height="160"
              width="160"
              ariaLabel="blocks-loading"
              wrapperStyle={{}}
              wrapperClass="blocks-wrapper"
              colors={['#00319f', '#2e56b1', '#5c7bc2', '#8aa1d3', '#b2c1e2']}
              />
            </div>
            <div className='logo'>
              <img src="https://app.liquid.diamonds/assets/LD_logo_only_square_long_dark_bg-e523d23a73890beed782b6e9596ba2ba.png" alt='LD logo' width='70px' height='70px'></img>
          </div>
        </div>)

    return (
      <div className="row justify-content-start border-secondary" style={{margin:'10px'}}>
        <div className="p-5 text-center">
          <h1 className="mb-3">Inventory Report</h1>
        </div>

        {showAlert ? <Bootstrap.Alert key='warning' variant='warning' onClose={() => this.setState({showAlert: false})}  dismissible style={{width: "80%", marginLeft: '10%'}}>
          Oops, we couldnâ€™t find any data. <br/>Please try with different some filters or params.
        </Bootstrap.Alert>: null}

        <div className="row text-left col-sm-3" style={{margin:'10px'}}>
          <Form>
            <Form.Group className="mb-3">  
              <Form.Check 
                inline='true'
                type='switch'
                label='Change BreakDown'
                name='change_breakdown'
                value = {this.state.filterHash.change_breakdown}
                onChange={this.handleBreakdownClick}/>
              {/*<Form.Check 
                inline='true'
                type="switch"
                label="Get Turn Data"
                name="get_turn"
                value = {this.state.filterHash.get_turn}
                onChange={this.handleTurnClick}/>
              {showInventoryDateInput ? <Form.Control  
                size='lg' 
                type='date'
                label='Inventory Date'
                name='new_inventory_date'
                value = {this.new_inventory_date.current}
                onChange={this.handleTurnDateClick}
                placeholder='Inventory Date'/> : <></>}*/}
              </Form.Group>
          </Form>
        </div>
        <div className="row justify-content-start border-secondary" style={{margin:'10px'}}>
          {Object.entries(this.state.options).map(([key, options]) => {
            return (
              <div className="col-sm-3" style={{padding:'10px'}}>
                <Multiselect
                  optionLabel={"Select "+key}
                  onSelect={this.onSelect}
                  onRemove={this.onSelect}
                  className={key}
                  options={options}
                  displayValue='name'
                  placeholder={"Select "+key}
                  />
              </div>
            )
          })}
        </div>

        <div className="action_buttons">
          <Button variant="info" onClick={this.calculateData}>Apply Filters</Button>
          {getTurn ? (
            <Button variant="info"><BiDownload size={30}/>Download Report
              <CSVLink data={this.getCsvData()} className='hidden' filename={"Inventory_Turn_Report_"+this.state.filterHash.inventory_date+".csv"}></CSVLink>
            </Button>) : <p></p> }
          <hr></hr>
        </div>
        <br></br>
        <div className="row border-secondary each-summary" >
            {Object.entries(this.state.reportSummary).map(([key, value]) => {
              return(
                <div className='col-sm-3 alert alert-info each-summary'>
                  <h5>{key}:</h5>
                  <h5>{value}</h5>
                </div>
              )
            })}
        </div>

        <br></br>
        <div className="breakdown">
          <h2 className="mb-3">BreakDown</h2>
        </div>
        {getTurn ? (<TurnReport items={this.state.itemsData} DataisLoaded={this.state.BreakdownDataLoaded}/>) : (<StaticReport items={this.state.itemsData} DataisLoaded={this.state.BreakdownDataLoaded}/>)}
      </div>
      )
  }
}
export default InventoryReport
