import React, {Component, createRef} from 'react'
import './report.css'
import StaticReport from './StaticReport';
import TurnReport from './TurnReport';
import Multiselect from 'multiselect-react-dropdown';
import Button from 'react-bootstrap/Button';
import { CSVLink, CSVDownload } from "react-csv";
import { BiDownload } from "react-icons/bi";
import { ColorRing } from  'react-loader-spinner'
import Form from 'react-bootstrap/Form';
import Cookies from 'js-cookie';
import * as Bootstrap from 'react-bootstrap';
import Moment from 'moment';
import ReactModal from 'react-modal';
import ExpandableRows from './ExpandableRows';
import DataTable from 'react-data-table-component';
import AgingReport from './components/AgingReport';



import "react-datepicker/dist/react-datepicker.css";

const SUMMARY_DATA_PROPERTIES = {'lab':'diamond#cert','shape': 'diamond#shape','color': 'diamond#color','clarity': 'diamond#clarity','carats': 'gem#carats','milky': 'diamond#milky','shade': 'diamond#shade','make': 'diamond#make','fluor': 'diamond#fluor','bic': 'diamond#black_in_center','bis': 'diamond#black_in_side','wis': 'diamond#white_in_side','wic': 'diamond#white_in_center'}
const ICON_HASH_FOR_SUMMERY = {'Total Price': ['bi-card-checklist', 'sales-card'], 'Average Price': ['bi-currency-dollar', 'revenue-card'], 'Count': ['bi-people' , 'customers-card']}
const availableSizes =  [50, 100, 250]
const CENTS_TO_DOLLAR_CONVERSION_FACTOR = 100.00
const REPORT_VALUE_MAPPING = {'STK':'attr_types.diamond#stock_number','CERT':'attr_types.diamond#cert_number','AGING':'aging_group', 'SHP':'attr_types.diamond#shape','COL':'attr_types.diamond#color','CLAR':'attr_types.diamond#clarity','CTS':'attr_types.gem#carats','MILKY':'attr_types.diamond#milky','SHADE':'attr_types.diamond#shade','MAKE':'attr_types.diamond#make','FLOUR':'attr_types.diamond#fluor','BIC':'attr_types.diamond#black_in_center','BIS':'attr_types.diamond#black_in_side','WIC':'attr_types.diamond#white_in_center','WIS':'attr_types.diamond#white_in_side','LAB':'attr_types.diamond#cert','RAP %':'attr_types.diamond#rap_percent','$/CT':'attr_types.gem#price_per_carat', 'Total': 'sell_offer_cents'}
const REPORT_HASH = {'inventory_report': [false, 'Inventory Report'], 'sale_report': [false, 'Sale Report'], 'aging_report': [true, 'Aging Report']}
const columns = [];
const DEFAULT_COLUMNS = {
  'LD ID' : row => row.id,
  'STK'   : row => row.item_attributes['diamond#stock_number'].trans || 'N/A',
  'CERT'  : row => row.item_attributes['diamond#cert_number'].trans || 'N/A',
  'AGING' : row => row.aging_group,
  'SHP'   : row => row.item_attributes['diamond#shape'].trans || 'N/A',
  'COL'   : row => row.item_attributes['diamond#color'].trans || 'N/A',
  'CLAR'  : row => row.item_attributes['diamond#clarity'].trans || 'N/A',
  'CTS'   : row => row.item_attributes['gem#carats'].trans || 'N/A',
  'MILKY' : row => row.item_attributes['diamond#milky'].trans || 'N/A',
  'SHADE' : row => row.item_attributes['diamond#shade'].trans || 'N/A',
  'MAKE'  : row => row.item_attributes['diamond#make'].trans || 'N/A',
  'FLOUR' : row => row.item_attributes['diamond#fluor'].trans || 'N/A',
  // 'BIC'   : row => row.item_attributes['diamond#black_in_center'].trans || 'N/A',
  // 'BIS'   : row => row.item_attributes['diamond#black_in_side'].trans || 'N/A',
  // 'WIC'   : row => row.item_attributes['diamond#white_in_center'].trans || 'N/A',
  // 'WIS'   : row => row.item_attributes['diamond#white_in_side'].trans || 'N/A',
  // 'LAB'   : row => row.item_attributes['diamond#cert'].trans || 'N/A',
  'RAP %' : row => row.item_attributes['diamond#rap_percent'].trans || 'N/A',
  '$/CT'  : row => row.item_attributes['gem#price_per_carat'].trans || 'N/A',
  'Total' : row => "$" + Math.round(row.sell_offer_cents/100),
  }

class InventoryReport extends Component {
  constructor(props) {
    super(props);
    this.expanableComponent = ({ data }) => <ExpandableRows data={data} isCsr={this.props.isCsr}/>

    const inv_date = Moment(new Date()).format('yyyy-MM-DD')
    this.initialState = {
      columns: columns,
      totalRows: 0,
      DataisLoaded: false,
      apiDataisLoaded: false,
      drilldownData: {},
      itemsData: {},
      options: {},
      reportSummary: {'Total Price': 0, 'Average Price': 0, 'Count': 0},
      DataisLoaded: false,
      BreakdownDataLoaded: false,
      showAlert: false,
      filterHash: {
        'size'                    : 1,
        'end_date'                : '',
        'start_date'              : '',
        'inventory_date'          : inv_date,
        'show_mine'               : true,
        'show_others'             : true,
        'seller_account_id'       : [],
        'get_filtered_data'       : true,
        'item_filter'             : {'discrete': {}},
        'get_only_matching_count' : true,
        'change_breakdown'        : false,
        'get_turn'                : false,
      },
      change_breakdown: false,
      inventory_date: inv_date,
      get_turn: false,
      showDrillDown: false
    }
    this.initialItemData = createRef(0)
    this.initialRankData = createRef(0)
    this.can_change_breakdown = false
    this.can_get_turn = false
    this.new_inventory_date = inv_date

    this.state = {...this.initialState };

    this.calculateData = this.calculateData.bind(this)
    this.onSelect = this.onSelect.bind(this)
    this.generateOptions = this.generateOptions.bind(this)
    this.captureUrls = this.captureUrls.bind(this)
    this.serialize = this.serialize.bind(this)
    this.getBreakdownData = this.getBreakdownData.bind(this)
    this.handleBreakdownClick = this.handleBreakdownClick.bind(this)
    this.handleTurnClick = this.handleTurnClick.bind(this)
    this.handleTurnDateClick = this.handleTurnDateClick.bind(this)
    this.requestForReports = this.requestForReports.bind(this)
    this.handleCheckClick = this.handleCheckClick.bind(this)
    this.handleShowDrillDown = this.handleShowDrillDown.bind(this)
    this.handleHideDrillDown = this.handleHideDrillDown.bind(this)
    this.collectDrilldownData = this.collectDrilldownData.bind(this)
  }

    doSort(column, sortDirection){ 
    if (column.name === 'AGING')
      return
    var filterHash = this.state.filterHash
    filterHash['sort_by'] = REPORT_VALUE_MAPPING[column.name]
    filterHash['asc_desc'] = sortDirection
    this.multi_sort = ''
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }
  handlePerRowsChange(sizePerPage){
    let filterHash = this.state.filterHash
    filterHash.size = sizePerPage
    filterHash.from = 0
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }

  handlePageChange(pageNumber){
    let filterHash = this.state.filterHash
    filterHash.from = filterHash.size * (pageNumber-1)
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }

  collectDrilldownData(){
    this.captureUrls()
    let filterHash  = this.state.filterHash
    let accessToken = Cookies.get('access_token')
    filterHash['get_only_matching_count'] = false
    filterHash['size']  = availableSizes[0]
    let queryParams = this.serialize(filterHash)
    fetch(process.env.REACT_APP_API_URL + "/items?"+ queryParams, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken }})
      .then(response => response.json())
      .then((response) => {
        if(response.meta.total <  1)
          this.setState({showAlert: true})
        if (response.errors)
          alert(response.errors.error)
        else
          this.setState({drilldownData: response.items,expandedComponent:JSON.stringify(response.item, null, 2),totalRows:response.meta.total,drilldownLoaded: true, apiDataisLoaded: true})
      })
  }

  handleShowDrillDown(){
    this.setState({showDrillDown: true})
    this.collectDrilldownData();
  }

  handleHideDrillDown(){
    this.setState({showDrillDown: false})
  }

  componentDidMount() {
    this.getBreakdownData();
    this.calculateData();
  }

  requestForReports(){
    // Api call for the request 
    // default msg and hide button
  }

  handleCheckClick(event){
    console.log(event)
    console.log(event.target.name)
  }


  getBreakdownData() {
    let accessToken = Cookies.get('access_token')
    this.captureUrls()
    let queryParams = this.state.filterHash
    fetch(process.env.REACT_APP_API_URL + "/inventory_report", {method: 'POST', headers: {'Authorization': 'Bearer '+ accessToken, 'Content-Type': 'application/json'}, body: JSON.stringify(queryParams)})
      .then(response => response.json())
      .then((response) => {
        this.generateOptions(response)
        if(Object.values(response).every(element => element.length == 0))
          this.setState({showAlert: true});
        this.setState({
          itemsData: response,
          DataisLoaded: true,
          BreakdownDataLoaded: true,
          });
        if (queryParams.get_turn){
          if (!queryParams.change_breakdown && !this.initialRankData.current){
            this.initialRankData.current = response;
          }
        }
        else{
          if (!queryParams.change_breakdown && !this.initialItemData.current){
            this.initialItemData.current = response;
          }
        }
      })
    }

  handleBreakdownClick(event){
    this.can_change_breakdown = event.target.checked
    this.setState({change_breakdown: event.target.checked})
  }
  handleTurnClick(event){
    this.setState({get_turn: event.target.checked})
  }
  handleTurnDateClick(event){
    this.setState({inventory_date: event.target.value})
