import React, { Component } from "react";
import ReactDOM from "react-dom";
const $ = require("jquery");
$.DataTable = require("datatables.net");

const columns = [];
const COLUMN_MAPPING = [
  {className: 'dt-control',orderable: false,data: null,defaultContent: ''},
  {title: 'LD ID', data: 'id'},
  {title: 'STK'  , data: 'item_attributes.diamond#stock_number.trans'},
  {title: 'CERT' , data: 'item_attributes.diamond#cert_number.trans'},
  {title: 'AGING' , data: 'aging_group'},
  {title: 'SHP'  , data: 'item_attributes.diamond#shape.trans'},
  {title: 'COL'  , data: 'item_attributes.diamond#color.trans'},
  {title: 'CLAR' , data: 'item_attributes.diamond#clarity.trans'},
  {title: 'CTS'  , data: 'item_attributes.gem#carats.trans'},
  {title: 'MILKY', data: 'item_attributes.diamond#milky.trans'},
  {title: 'SHADE', data: 'item_attributes.diamond#shade.trans'},
  {title: 'MAKE' , data: 'item_attributes.diamond#make.trans'},
  {title: 'FLOUR', data: 'item_attributes.diamond#fluor.trans'},
  {title: 'BIC'  , data: 'item_attributes.diamond#black_in_center.trans'},
  {title: 'BIS'  , data: 'item_attributes.diamond#black_in_side.trans'},
  {title: 'WIC'  , data: 'item_attributes.diamond#white_in_center.trans'},
  {title: 'WIS'  , data: 'item_attributes.diamond#white_in_side.trans'},
  {title: 'LAB'  , data: 'item_attributes.diamond#cert.trans'},
  {title: 'RAP %', data: 'item_attributes.diamond#rap_percent.trans'},
  {title: '$/CT' , data: 'item_attributes.gem#price_per_carat.trans'},
  {title: 'Total', data: 'sell_offer_cents', render: function ( data, type, row ) {return '$' + (data/100)}}]

class MyDataTable extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: this.props.data,
      columns: COLUMN_MAPPING
    }
  }

  componentDidMount() {
    $('#dataTable1').dataTable()

    this.$el = $(this.el);
    const table = this.$el.DataTable({
      responsive: true,
      displaylength: 100,
      fixedHeader: true,
      order: [],
      data: this.state.data,
      columns: this.state.columns,
      pagingType: 'full_numbers',
      columnDefs: [{
        targets: -1,
        className: 'order-column'
      },
      {
        targets: [ 12,13,14,15 ],
        visible: false,
      }],
      lengthMenu: [50,100,200, 'All']
    });

    $('#dataTable tbody').on('click', 'td.dt-control', function () {

        var tr = $(this).closest('tr');
        var row = table.row(tr);

        if (row.child.isShown()) {
            row.child.hide();
            tr.removeClass('show');
        } else {
            row.child(
              '<div className="row">'+
              '<div className="col-6">'+
              '<div className="row">'+
              '<div className="col-sm-4 text-left font-weight-bold">ID</div>'+
              '<div className="col-sm-8 text-left">: {row.data().id}</div> '+
              '</div>'+
              '</div>'+
              '</div>'+
              '<table cellpadding="5" cellspacing="0" border="0" style="padding-left:50px;">' +
              '<tr>' +
              '<td>Full name:</td>' +
              '<td>' +row.data().id +
              '</td>' +
              '</tr>' +
              '<tr>' +
              '<td>Extension number:</td>' +
              '<td>' +
              row.data().aging_group +
              '</td>' +
              '</tr>' +
              '<tr>' +
              '<td>Extra info:</td>' +
              '<td>And any further details here (images etc)...</td>' +
              '</tr>' +
              '</table>'
            ).show();
            tr.addClass('show');
        }
    });
  }

  reloadTableData = (data) => {
    const table = $('.data-table-wrapper').find('table').DataTable();
    table.clear();
    table.rows.add(data);
    table.draw();
  }

  shouldComponentUpdate(nextProps, nextState){
    if (nextProps.data.length !== this.state.data.length) {
      this.reloadTableData(nextProps.data);
    }
    return false;
  }

  render() {
    return (
      <div className="row">
        <div className="col-lg-12">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">DataTable</h5>
            {/*<table className="table table-borderless table-sm table-hover" id="dataTable1" ref={(el) => (this.el = el)}>
              <thead>
                <tr>

                  {COLUMN_MAPPING.map(element => {
                    return (<th scope="col">{element.title}</th>)
                  })}
                </tr>
              </thead>
              <tbody>  
                {this.state.data.map(element => {
                  return (<tr>
                    <td className="dt-control"/>
                    <td scope='row'>{element.id}</td>
                    <td scope='row'>{element.item_attributes['diamond#stock_number'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#cert_number'].trans}</td>
                    <td scope='row'>{element.aging_group}</td>
                    <td scope='row'>{element.item_attributes['diamond#shape'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#color'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#clarity'].trans}</td>
                    <td scope='row'>{element.item_attributes['gem#carats'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#milky'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#shade'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#make'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#fluor'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#black_in_center'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#black_in_side'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#white_in_center'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#white_in_side'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#cert'].trans}</td>
                    <td scope='row'>{element.item_attributes['diamond#rap_percent'].trans}</td>
                    <td scope='row'>{element.item_attributes['gem#price_per_carat'].trans}</td>
                    <td scope='row'>{"$" + Math.round(element.sell_offer_cents/100)}</td>
                  </tr>)
                })}
              </tbody>
            </table>*/}
            <table className="table table-borderless display" id="dataTable" ref={(el) => (this.el = el)} />
          </div>
        </div>      

      </div>

      </div>

    );
  }
}
export default MyDataTable;
