import React from 'react';
import { Route, Routes, BrowserRouter } from 'react-router-dom';
// import './App.css';
//import 'assets/js/main.js';

import SaleReport from './components/SaleReport';
import InventoryReport from './components/InventoryReport';
import AgingReport from './components/AgingReport';
import Cookies from 'js-cookie';
import * as Icon from 'react-bootstrap-icons';

const session_api = '/my_profile'
const MY_ROUTES = {'/inventory_report': 'Inventory Report' , '/sale_report': 'Sale Report', '/aging_report': 'Aging Report'}


  class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      userDetails: {},
      tokenLoaded: false
    }
    //this.verifyToken()
  }

  verifyToken(){  
    let userDetails
    const queryParams = new URLSearchParams(window.location.search);
    var accessToken = queryParams.get('access_token')
    if(accessToken){
      document.cookie = "access_token="+ accessToken
      queryParams.delete('access_token')
      window.location.search = queryParams
    }
    else{
      accessToken = Cookies.get('access_token')
      if(!accessToken){
        alert('Session Expired, Please login again')
        window.location = process.env.REACT_APP_AUTH_URL
        return 0;
      }
    }
    fetch(process.env.REACT_APP_API_URL + session_api, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken}})
      .then(response => response.json())
      .then((response) => {
        if (response.error){
          alert('Session Expired, Please login again')
          window.location = process.env.REACT_APP_AUTH_URL
        }
        else{
          userDetails = response.data
          this.setState({userDetails: userDetails, tokenLoaded: true})
        }
      })
      .catch((error) => {
        console.log("Something Went Wrong..." + error)
      })
  }

  componentDidMount() {
    console.log("this is from app.js")
    alert(this.state.userDetails.length == 0)
    const script = document.createElement("script")
    script.src = 'assets/js/main.js'
    script.async = true
    document.body.appendChild(script)
    let userDetails
    const queryParams = new URLSearchParams(window.location.search);
    var accessToken = queryParams.get('access_token')
    if(accessToken){
      document.cookie = "access_token="+ accessToken
      queryParams.delete('access_token')
      window.location.search = queryParams
    }
    else{
      accessToken = Cookies.get('access_token')
      if(!accessToken){
        alert('Session Expired, Please login again')
        window.location = process.env.REACT_APP_AUTH_URL
        return 0;
      }
    }
    fetch(process.env.REACT_APP_API_URL + session_api, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken}})
      .then(response => response.json())
      .then((response) => {
        if (response.error){
          alert('Session Expired, Please login again')
          window.location = process.env.REACT_APP_AUTH_URL
        }
        else{
          userDetails = response.data
          this.setState({userDetails: userDetails})
        }
      })
      .catch((error) => {
        console.log("Something Went Wrong..." + error)
      })
  }

  render() {
    let search = window.location.search
    const urlParams = new URLSearchParams(search);
    let sellerIds = urlParams.get('sellerIds')
    let searchParams =  (sellerIds) ?  '?sellerIds='+sellerIds : ''
    const {userDetails, tokenLoaded} = this.state;

    let isCsr = this.state.userDetails.roles && this.state.userDetails.roles.includes('csr')
    return (
      <div>
        <header id="header" className="header fixed-top d-flex align-items-center">
          <div className="d-flex align-items-center justify-content-between">
            <a className="logo d-flex align-items-center">
              <img src="assets/img/logo.png" alt=""/>
              <span className="d-none d-lg-block">LD Analytics</span>
            </a>
            <i className="bi bi-list toggle-sidebar-btn"></i>
          </div>

          <nav className="header-nav ms-auto">
            <ul className="d-flex align-items-center">
              <li className="nav-item dropdown pe-3">

                <a className="nav-link nav-profile d-flex align-items-center pe-0" href="#" data-bs-toggle="dropdown">
                  <img src="assets/img/profile-img.jpg" alt="Profile" className="rounded-circle"/>
                  <span className="d-none d-md-block dropdown-toggle ps-2">{userDetails.name}</span>
                </a>

                <ul className="dropdown-menu dropdown-menu-end dropdown-menu-arrow profile">
                  <li className="dropdown-header">
                    <h6>{(userDetails.name) ? userDetails.name : 'NA'}</h6>
                    <span>{(userDetails.roles) ? userDetails.roles.join(', ') : 'NA'}</span>
                  </li>
                  <li>
                    <hr className="dropdown-divider"/>
                  </li>

                  <li>
                    <a className="dropdown-item d-flex align-items-center" href={process.env.REACT_APP_API_URL+"/#/users/2"}  target="_blank">
                      <i className="bi bi-person"></i>
                      <span>My Profile</span>
                    </a>
                  </li>
                  <li>
                    <hr className="dropdown-divider"/>
                  </li>

                  <li>
                    <a className="dropdown-item d-flex align-items-center">
                      <i className="bi bi-envelope"></i>
                      <span>{userDetails.email || 'NA' }</span>
                    </a>
                  </li>
                  <li>
                    <hr className="dropdown-divider"/>
                  </li>

                  <li>
                    <a className="dropdown-item d-flex align-items-center">
                      <i className="bi bi-telephone"></i>
                      <span>{userDetails.phone || 'NA'}</span>
                    </a>
                  </li>
                  <li>
                    <hr className="dropdown-divider"/>
                  </li>

                  <li>
                    <a className="dropdown-item d-flex align-items-center">
                      <i className="bi bi-check-circle"></i>
                      <span>{userDetails.state || 'NA'}</span>
                    </a>
                  </li>
                </ul>
              </li>
            </ul>
          </nav>
        </header>

        <aside id="sidebar" className="sidebar">
          <ul className="sidebar-nav" id="sidebar-nav">
            {Object.entries(MY_ROUTES).map(([route, name]) => {
                let is_active_class = (route == window.location.pathname) ? '' : 'collapsed'
                return(
                  <li className={"nav-item"+ route}>
                    <a className={"nav-link "+ is_active_class} href={route + searchParams}>
                      <i className="bi bi-journal-text"></i>
                      <span>{name}</span>
                    </a>
                  </li>
                )}
              )}
          </ul>
        </aside>
        <main id="main" className="main">
          <div className="pagetitle">
            <h1>{MY_ROUTES[window.location.pathname]}</h1>
            <nav>
              <ol className="breadcrumb">
                <li className="breadcrumb-item"><a href="index.html">Home</a></li>
                <li className="breadcrumb-item active">{MY_ROUTES[window.location.pathname]}</li>
              </ol>
            </nav>
          </div>

          <BrowserRouter>
            {userDetails && <Routes>
              <Route path="/sale_report" element={[<SaleReport isCsr={isCsr}/>]} />
              <Route path="/inventory_report" element={[<InventoryReport isCsr={isCsr}/>]} />
              <Route path="/aging_report" element={[<AgingReport isCsr={isCsr}/>]} />
            </Routes>}
          </BrowserRouter>
        </main>
        <a href="#" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short"></i></a>
      </div>
    )
  }
}
export default App;
