import React, {Component} from 'react'
import './report.css'
import { ColorRing } from  'react-loader-spinner'
import Cookies from 'js-cookie';
import DataTable from 'react-data-table-component';
import * as Bootstrap from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';

import ExpandableRows from './ExpandableRows';
import MyDataTable from './MyDataTable';

const $ = require('jquery')
$.DataTable = require('jquery')


const availableSizes =  [10,50, 100, 250]
const CENTS_TO_DOLLAR_CONVERSION_FACTOR = 100.00
const REPORT_VALUE_MAPPING = {'STK':'attr_types.diamond#stock_number','CERT':'attr_types.diamond#cert_number','AGING':'aging_group', 'SHP':'attr_types.diamond#shape','COL':'attr_types.diamond#color','CLAR':'attr_types.diamond#clarity','CTS':'attr_types.gem#carats','MILKY':'attr_types.diamond#milky','SHADE':'attr_types.diamond#shade','MAKE':'attr_types.diamond#make','FLOUR':'attr_types.diamond#fluor','BIC':'attr_types.diamond#black_in_center','BIS':'attr_types.diamond#black_in_side','WIC':'attr_types.diamond#white_in_center','WIS':'attr_types.diamond#white_in_side','LAB':'attr_types.diamond#cert','RAP %':'attr_types.diamond#rap_percent','$/CT':'attr_types.gem#price_per_carat', 'Total': 'sell_offer_cents'}
const REPORT_HASH = {'inventory_report': [false, 'Inventory Report'], 'sale_report': [false, 'Sale Report'], 'aging_report': [true, 'Aging Report']}
const columns = [];
const DEFAULT_COLUMNS = {
  'LD ID' : row => row.id,
  'STK'   : row => row.item_attributes['diamond#stock_number'].trans || 'N/A',
  'CERT'  : row => row.item_attributes['diamond#cert_number'].trans || 'N/A',
  'AGING' : row => row.aging_group,
  'SHP'   : row => row.item_attributes['diamond#shape'].trans || 'N/A',
  'COL'   : row => row.item_attributes['diamond#color'].trans || 'N/A',
  'CLAR'  : row => row.item_attributes['diamond#clarity'].trans || 'N/A',
  'CTS'   : row => row.item_attributes['gem#carats'].trans || 'N/A',
  'MILKY' : row => row.item_attributes['diamond#milky'].trans || 'N/A',
  'SHADE' : row => row.item_attributes['diamond#shade'].trans || 'N/A',
  'MAKE'  : row => row.item_attributes['diamond#make'].trans || 'N/A',
  'FLOUR' : row => row.item_attributes['diamond#fluor'].trans || 'N/A',
  // 'BIC'   : row => row.item_attributes['diamond#black_in_center'].trans || 'N/A',
  // 'BIS'   : row => row.item_attributes['diamond#black_in_side'].trans || 'N/A',
  // 'WIC'   : row => row.item_attributes['diamond#white_in_center'].trans || 'N/A',
  // 'WIS'   : row => row.item_attributes['diamond#white_in_side'].trans || 'N/A',
  // 'LAB'   : row => row.item_attributes['diamond#cert'].trans || 'N/A',
  'RAP %' : row => row.item_attributes['diamond#rap_percent'].trans || 'N/A',
  '$/CT'  : row => row.item_attributes['gem#price_per_carat'].trans || 'N/A',
  'Total' : row => "$" + Math.round(row.sell_offer_cents/100),
  }

  const agingGroupSort = (rowA, rowB) => {
    const a = rowA.title.split('/-|+/')[0];
    const b = rowB.title.split('/-|+/')[0];
    if (a > b) {return 1;}
    if (b > a) {return -1;}
    return 0;
  };

Object.entries(DEFAULT_COLUMNS).map(([key, selector_attr]) => {
      columns.push({
        name: key,
        title: key,
        data: 'account_id',
        selector: selector_attr,
        sortable: (REPORT_VALUE_MAPPING[key] ? true : false)
      })
    })

class AgingReport extends Component {
  constructor(props) {
    super(props);
    this.expanableComponent = ({ data }) => <ExpandableRows data={data} isCsr={this.props.isCsr}/>


    this.state = {
      expandedComponent: '<p></p>',  
      itemsData: {},
      columns: columns,
      totalRows: 0,
      DataisLoaded: false,
      apiDataisLoaded: false,
      showAlert: false,
      filterHash:  {
        'pageNumber'              : 0, 
        'from'                    : 0,
        'size'                    : availableSizes[0],
        'show_mine'               : true,
        'show_others'             : true,
        'seller_account_id'       : '',
        'get_filtered_data'       : true,
        'item_filter'             : {'discrete': {'state':['for_sale']}},
        'get_only_matching_count' : false,
        'change_breakdown'        : false,
        'get_turn'                : false,
        'sort_by'                 : 'sell_offer_cents',
        'asc_desc'                : 'asc'
      }
    };
    this.multi_sort = "&multi_sort%5B0%5D%5Battr_types.diamond%23shape%5D=asc&multi_sort%5B1%5D%5Bcarat_ranges%5D=asc&multi_sort%5B2%5D%5Battr_types.diamond%23color%5D=asc&multi_sort%5B3%5D%5Battr_types.diamond%23clarity%5D=asc"
    this.captureUrls  = this.captureUrls.bind(this)
    this.collectAgingData  = this.collectAgingData.bind(this)
    this.handlePerRowsChange = this.handlePerRowsChange.bind(this)
    this.handlePageChange = this.handlePageChange.bind(this)
    this.doSort = this.doSort.bind(this)
 }

  doSort(column, sortDirection){ 
    if (column.name === 'AGING')
      return
    var filterHash = this.state.filterHash
    filterHash['sort_by'] = REPORT_VALUE_MAPPING[column.name]
    filterHash['asc_desc'] = sortDirection
    this.multi_sort = ''
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }
  componentDidMount() {
    this.collectAgingData()
  }

  handlePerRowsChange(sizePerPage){
    let filterHash = this.state.filterHash
    if(isNaN(sizePerPage))
      sizePerPage = availableSizes[sizePerPage.target.selectedIndex]
    filterHash.size = sizePerPage
    filterHash.from = 0
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }

  handlePageChange(pageNumber){
    let filterHash = this.state.filterHash
    filterHash.from = filterHash.size * (pageNumber-1)
    this.setState({filterHash: filterHash, apiDataisLoaded: false})
    this.collectAgingData()
  }

  collectAgingData(){
    this.captureUrls()
    let accessToken = Cookies.get('access_token')
    console.log(this.state.filterHash)
    let queryParams = this.serialize(this.state.filterHash) + this.multi_sort
    fetch(process.env.REACT_APP_API_URL + "/items.json?"+ queryParams, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken}})
      .then(response => response.json())
      .then((response) => {
        if(response.meta.total <  1)
          this.setState({showAlert: true})
        if (response.errors)
          alert(response.errors.error)
        else
          this.setState({itemsData: response.items,expandedComponent:JSON.stringify(response.item, null, 2),totalRows:response.meta.total,DataisLoaded: true, apiDataisLoaded: true})
      })
  }

  serialize(obj, prefix) {
    var str = [],
    p,m;
    for (p in obj) {
      if (obj.hasOwnProperty(p)) {
        if (!isNaN(p)){
          m=''
        }else{
          m=p
        }
        var k = prefix ? prefix + "["+m+"]" : p,
          v = obj[p];
        str.push((v !== null && typeof v === "object") ?
          this.serialize(v, k) :
          encodeURIComponent(k) + "=" + encodeURIComponent(v));
      }
    }
    return str.join("&");
  }

  captureUrls(){
    const queryParams = new URLSearchParams(window.location.search);
    let sellerIds
    let filterHash = this.state.filterHash
    var sellerIdString = queryParams.get('sellerIds')
    if (sellerIdString){
      sellerIds = sellerIdString.split(',');
      filterHash['seller_account_id'] = sellerIds
    }
    this.setState({
      filterHash: filterHash
    })
  }

  render(){
    const { DataisLoaded, apiDataisLoaded, itemsData, columns, totalRows, expandedComponent, filterHash  } = this.state;
    const tableOpacity = apiDataisLoaded ? 1 : 0.5 
    const displayLoader = apiDataisLoaded ? 'none' : 'block' 
    let showAlert = (totalRows < 1 && this.state.showAlert)
    if (!DataisLoaded)
      return(

        <div className='row'>
          <div className="align-items-center justify-content-center">
            <h3 className=" text-center"> Fetching reports for you.... </h3>
          </div>
          <ColorRing
            visible={true}
            height="60"
            width="60"
            ariaLabel="blocks-loading"
            wrapperStyle={{}}
            wrapperClass="blocks-wrapper"
            colors={['#00319f', '#2e56b1', '#5c7bc2', '#8aa1d3', '#b2c1e2']}
          />
        </div>)
    return (
      <div className="row justify-content-start border-secondary">

        {showAlert ?
          <Bootstrap.Alert key='warning' variant='warning' onClose={() => this.setState({showAlert: false})}  dismissible style={{width: "80%", marginLeft: '10%'}}>
          Oops, we couldn’t find any data.
        </Bootstrap.Alert>:
        <div>

        <div className='col-lg-12'>  

        </div>
        <br/>

        <div className="row">
          <div className="col-lg-12">
            <div className="card">
              <div className="card-body"> 
                <div className='row col-xxl-2 col-md-6 card-title'>
                  <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#verticalycentered"><i className=" bi bi-download me-1"></i> Download Report</button>
                  <div className="modal fade" id="verticalycentered" tabIndex="-1">
                    <div className="modal-dialog modal-dialog-centered">
                      <div className="modal-content">
                        <div className="modal-header">
                          <h5 className="modal-title">Reports to Download</h5>
                          <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                          <div className="row mb-3">
                            <div className="col-sm-10">
                              {Object.entries(REPORT_HASH).map(([key, value_n_name]) => {
                                return (<div className="form-check">
                                  <input className="form-check-input" type="checkbox" id={key} name={key} defaultChecked={value_n_name[0]} onChange={this.handleCheckClick}/>
                                  <label className="form-check-label" htmlFor="gridCheck1">{value_n_name[1]}</label>
                                </div> 
                              )})}
                            </div>
                          </div>

                        </div>
                        <div className="modal-footer">
                          <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                          <button type="button" className="btn btn-primary" onClick={this.requestForReports}>Get Reports</button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>  
                <hr/>
                <DataTable
                  columns={columns}
                  data={itemsData}
                  onSort={this.doSort}
                  expandableRows
                  expandableRowsComponent={this.expanableComponent}
                  pagination
                  paginationServer
                  paginationTotalRows={totalRows}
                  paginationPerPage={availableSizes[0]}
                  paginationRowsPerPageOptions={availableSizes}
                  onChangePage={this.handlePageChange}
                  onChangeRowsPerPage={this.handlePerRowsChange}
                  highlightOnHover={true}
                  expandOnRowClicked={true}
                />
              </div>
            </div>
          </div>

          {/*<MyDataTable columns={columns} data={itemsData}/>*/}
        </div>
        <div style={{display: displayLoader, zIndex:2, margin:'00px'}}>
            <div className='loader'>
              <ColorRing
                visible={true}
                height="80"
                width="80"
                ariaLabel="blocks-loading"
                wrapperStyle={{}}
                wrapperClass="blocks-wrapper"
                colors={['#00319f', '#2e56b1', '#5c7bc2', '#8aa1d3', '#b2c1e2']}
                />
              </div>
              <div>
              <h1 className="mb-3"> Please wait.... </h1>
            </div>
          </div>
        </div>
      }
      </div>
      )
  }
}
export default AgingReport
