import React, {Component} from 'react'
import './report.css'
import StaticReport from './StaticReport';
import Multiselect from 'multiselect-react-dropdown';
import { ColorRing } from  'react-loader-spinner'
import * as Bootstrap from 'react-bootstrap';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'moment';
import Cookies from 'js-cookie';
import AgingReport from './AgingReport';
import ExpandableRows from './ExpandableRows';

const SUMMARY_DATA_PROPERTIES = {'lab':'diamond#cert','shape': 'diamond#shape','color': 'diamond#color','clarity': 'diamond#clarity','carats': 'gem#carats','milky': 'diamond#milky','shade': 'diamond#shade','make': 'diamond#make','fluor': 'diamond#fluor','bic': 'diamond#black_in_center','bis': 'diamond#black_in_side','wis': 'diamond#white_in_side','wic': 'diamond#white_in_center'}
const ICON_HASH_FOR_SUMMERY = {'Total Price': ['bi-card-checklist', 'sales-card'], 'Average Price': ['bi-currency-dollar', 'revenue-card'], 'Count': ['bi-people' , 'customers-card']}
const REPORT_HASH = {'inventory_report': [false, 'Inventory Report'], 'sale_report': [true, 'Sale Report'], 'aging_report': [false,'Aging Report']}
const availableSizes =  [50, 100, 250]
const DEFAULT_REPORT_DOWNLOAD_PARAMS = {
  "end_date":Moment(new Date()).format('yyyy-MM-DD'),
  "start_date":Moment(new Date(new Date().setMonth(new Date().getMonth() - 1))).format('yyyy-MM-DD'),
  "inventory_date": Moment(new Date()).format('yyyy-MM-DD'),
  "seller_account_id":[]}

class SaleReport extends Component {
  constructor(props) {
    super(props);
    const end_date = Moment(new Date()).format('yyyy-MM-DD')
    const start_date = Moment(new Date(new Date().setMonth(new Date().getMonth() - 1))).format('yyyy-MM-DD');
    this.state = {
      itemsData: {},
      initialItemData: {},
      options: {},
      reportSummary: {'Total Price': 0, 'Average Price': 0, 'Count': 0},
      DataisLoaded: false,
      BreakdownDataLoaded: false,
      showAlert: false,
      filterHash: {
        'size'                    : 1,
        'end_date'                : end_date,
        'start_date'              : start_date, 
        'show_mine'               : true,
        'show_others'             : true,
        'is_sale_report'          : true,
        'seller_account_id'       : [],
        'get_filtered_data'       : true,
        'item_filter'             : {'discrete': {}},
        'get_only_matching_count' : true,
        'change_breakdown'        : false
      },
      start_date: start_date,
      end_date: end_date,
      showDrillDown: false
    }
    this.can_change_breakdown = false

    this.initialItemData = {}
    this.calculateData = this.calculateData.bind(this)
    this.onSelect = this.onSelect.bind(this)
    this.generateOptions = this.generateOptions.bind(this)
    this.captureUrls = this.captureUrls.bind(this)
    this.serialize = this.serialize.bind(this)
    this.getBreakdownData = this.getBreakdownData.bind(this)
    this.handleBreakdownClick = this.handleBreakdownClick.bind(this)
    this.handleStartDateClick = this.handleStartDateClick.bind(this)
    this.handleEndDateClick = this.handleEndDateClick.bind(this)
    this.updateURL = this.updateURL.bind(this)
    this.requestForReports = this.requestForReports.bind(this)
    this.handleCheckClick = this.handleCheckClick.bind(this)
    this.handleShowDrillDown = this.handleShowDrillDown.bind(this)
    this.handleHideDrillDown = this.handleHideDrillDown.bind(this)
    this.captureReportDownloadParams = this.captureReportDownloadParams.bind(this)

  }

  handleShowDrillDown(){
    this.setState({showDrillDown: true})
  }
  handleHideDrillDown(){
    this.setState({showDrillDown: false})
  }

  captureReportDownloadParams(){
    let paramsHash = {}
    let filterHash =  this.state.filterHash
    paramsHash['seller_account_id'] = filterHash.seller_account_id
    paramsHash['download_inv_report']  = this.state.download_inventory_report
    paramsHash['download_sale_report'] = this.state.download_sale_report
    paramsHash['download_aging_report'] = this.state.download_aging_report
    paramsHash['download_aging_report'] = this.state.download_aging_report
    paramsHash['start_date'] = filterHash.start_date
    paramsHash['end_date'] = filterHash.end_date
    paramsHash['inventory_date'] = filterHash.end_date
    return paramsHash
  }
  requestForReports(){
  }

  handleCheckClick(event){
    console.log(event)
    console.log(event.target.name)
  }

  updateURL(){
    var new_params = []
    let filterHash = this.state.filterHash
    let sellerIdString = filterHash['seller_account_id'].join(',')
    let startDate = filterHash['start_date']
    let endDate = filterHash['end_date']
    if (sellerIdString){
      new_params.push("sellerIds="+sellerIdString)

    }
    if (startDate){
      new_params.push("startDate=" + startDate)

    }
    if (endDate){
      new_params.push("endDate="+endDate)

    }
    let new_url = window.location.protocol + '//' + window.location.host+window.location.pathname+'?' + new_params.join('&')  
    window.history.pushState({path:new_url}, '', new_url)
  }

  handleBreakdownClick(event){
    this.can_change_breakdown = event.target.checked
  }

  handleStartDateClick(event){
    this.setState({start_date: event.target.value})
  }

  handleEndDateClick(event){
    this.setState({end_date: event.target.value})
  }

  componentDidMount() {
    this.getBreakdownData();
    this.calculateData();
  }

  getBreakdownData() {
    let accessToken = Cookies.get('access_token')
    this.captureUrls()
    let queryParams = this.state.filterHash
    fetch(process.env.REACT_APP_API_URL + "/inventory_report", {method: 'POST', headers: {'Authorization': 'Bearer '+ accessToken, 'Content-Type': 'application/json'}, body: JSON.stringify(this.state.filterHash)})
      .then(response => response.json())
      .then((response) => {
        if(Object.values(response).every(element => element.length == 0))
          this.setState({showAlert: true});
        this.setState({
            itemsData: response,
            DataisLoaded: true,
            BreakdownDataLoaded: true
        });
        if(Object.keys(this.initialItemData).length === 0)
          this.initialItemData = response;
        this.generateOptions(response)
      })
    }

  captureUrls(){
    const queryParams = new URLSearchParams(window.location.search);
    let sellerIds, startDate, endDate, changeBreakdown
    let filterHash = this.state.filterHash
    var sellerIdString = queryParams.get('sellerIds')
    if (sellerIdString){
      sellerIds = sellerIdString.split(',');
      filterHash['seller_account_id'] = sellerIds
    }
    startDate = queryParams.get('startDate')
    if (startDate){
      this.setState({startDate: startDate})
      filterHash['start_date'] = startDate
    }
    endDate = queryParams.get('endDate')
    if (endDate){
      this.setState({endDate: endDate})
      filterHash['end_date'] = endDate
    }
    this.setState({
      filterHash: filterHash
    })
  }

  serialize(obj, prefix) {
    var str = [],
    p,m;
    for (p in obj) {
      if (obj.hasOwnProperty(p)) {
        if (!isNaN(p)){
          m=''
        }else{
          m=p
        }
        var k = prefix ? prefix + "["+m+"]" : p,
        v = obj[p];
        str.push((v !== null && typeof v === "object") ?
          this.serialize(v, k) :
          encodeURIComponent(k) + "=" + encodeURIComponent(v));
      }
    }
    return str.join("&");
  }

  generateOptions(response){
    Object.entries(response).map(([key, options]) => {
      options.map(data=>{
        data['className'] = key
      })
    })
    this.setState({
      options: response
    })
  }

  calculateData(event){
    this.setState({BreakdownDataLoaded: false})
    let filterHash = this.state.filterHash
    let canCallBreakdown = false
    if (this.state.start_date){
      if (this.state.start_date !== filterHash.start_date)
        canCallBreakdown = true
      filterHash.start_date = this.state.start_date
    }
    else{
      alert('Please select Start Date')
      return(0)
    }

    if (this.state.end_date){
      if (this.state.end_date !== filterHash.end_date)
        canCallBreakdown = true
      filterHash.end_date = this.state.end_date
    }
    else {
      alert('Please select End Date')
      return(0)
    }

    filterHash.change_breakdown =  this.can_change_breakdown
    if(canCallBreakdown)
      this.initialItemData = {}

    this.setState({
      filterHash: filterHash
    })

    let accessToken = Cookies.get('access_token')
    console.log(this.state.filterHash)
    this.updateURL()
    let queryParams = this.serialize(this.state.filterHash)
    fetch(process.env.REACT_APP_API_URL + "/items?"+ queryParams, {method: 'GET', headers: {'Authorization': 'Bearer '+ accessToken}})
      .then(response => response.json())
      .then((response) => {
        let reportSummary = this.state.reportSummary
        reportSummary['Count'] = response.meta.total
        reportSummary['Total Price'] = response.meta.aggregations.total_sum.slice(0,-3)
        reportSummary['Average Price'] = response.meta.aggregations.average.slice(0,-3)
        this.setState({
          reportSummary: reportSummary
        });
      })
    if (filterHash.change_breakdown || canCallBreakdown)
      this.getBreakdownData();
    else
      this.setState({itemsData: this.initialItemData, BreakdownDataLoaded: true});
  }

  onSelect(selectedList, removedItem) {
    var filterValues = []
    var attrName = null
    if (selectedList.length == 0 ){
      attrName = SUMMARY_DATA_PROPERTIES[removedItem.className]
    }  
    Object.entries(selectedList).map(([key, option]) => {
      attrName = SUMMARY_DATA_PROPERTIES[option.className]
      if (attrName) {
        if (attrName === "gem#carats"){
          let carat_range = option.key.split('-')
          filterValues.push({'to':carat_range[0], 'from':carat_range[1]})
        }
        else{
          filterValues.push(option.key)
        }
      }
    })
    let filterHash = this.state.filterHash
    filterHash['item_filter']['discrete'][attrName] = filterValues.flat()
    this.setState({
      filterHash: filterHash
    })
  }

  render(){
    const { DataisLoaded, items, showDrillDown } = this.state;
    let noRecords = Object.values(this.state.itemsData).every(element => element.length == 0)
    let showAlert = noRecords && this.state.showAlert
    let drilldownClass = showDrillDown ? 'show' : ''
    let drilldownStyle = { display: showDrillDown ? 'block' : 'none'}

    if (!DataisLoaded)
      return(
        <div className='row'>
          <div className="align-items-center justify-content-center">
            <h3 className=" text-center"> Fetching reports for you.... </h3>
          </div>
          <ColorRing
            visible={true}
            height="60"
            width="60"
            ariaLabel="blocks-loading"
            wrapperStyle={{}}
            wrapperClass="blocks-wrapper"
            colors={['#00319f', '#2e56b1', '#5c7bc2', '#8aa1d3', '#b2c1e2']}
          />
        </div>)

    return (
      <div className="row justify-content-start border-secondary">
        {showAlert ? 
          <div className="alert alert-warning alert-dismissible fade show text-center" role="alert">
            <i className="bi bi-exclamation-triangle me-1"></i>
              Oops, we couldn’t find anything. <br/>Please select different start and end date to get Results.
            <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
          </div>: null}
        <br></br>

        <div className='filters card multiselect-dropdown'>  
          <div className="text-left">
            <div className="row form-check form-switch multiselect-dropdown">
              <div className='form-check-inline col-sm-2'>
                <input className="form-check-input" type="checkbox" id="breakdownSwitch" name='change_breakdown' value={this.state.filterHash.change_breakdown} onChange={this.handleBreakdownClick}/>
                <label className="form-check-label" htmlFor="breakdownSwitch">Change BreakDown</label>
              </div>
            </div>
            <div className="row">
              <div className='col-sm-2'>
                <label htmlFor="inputDate" class="col-form-label">Start Date</label>
              </div>
              <div className='col-sm-2'>
                <label htmlFor="inputDate" class="col-form-label">End Date:</label>
              </div>
            </div>
            <div className='row'>
              <div class="col-sm-2 ">
                <input type="date" class="form-control" value={this.state.start_date} dateformat="dd/MM/yyyy" onChange={this.handleStartDateClick}/>
              </div>

              <div class="col-sm-2 ">
                <input type="date" class="form-control" value={this.state.end_date} dateformat="dd/MM/yyyy" onChange={this.handleEndDateClick}/>
              </div>
            </div>
            <br/>
          </div>


        {noRecords ? null :
        <div className="row mb-3 ">
          {Object.entries(this.state.options).map(([key, options]) => {
            return (
              <div className="col-sm-3 multiselect-dropdown">
                <Multiselect
                  style={{backgroundColor:'#4154f1'}}
                  optionLabel={"Select "+key}
                  onSelect={this.onSelect}
                  onRemove={this.onSelect}
                  className={key}
                  options={options}
                  displayValue='name'
                  placeholder={"Select "+key}
                  />
              </div>
            )
          })}
          </div>}
        </div>
        <div className='col-lg-12'>  
          <div className="row">
            <div className='col-xxl-2 col-md-6'>
              <button type="button" class="btn btn-primary" onClick={this.calculateData}><i class=" bi bi-sliders me-1"></i> Apply Filters</button>
            </div>
            <div className='col-xxl-2 col-md-6'>
              <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#verticalycentered"><i className=" bi bi-download me-1"></i> Download Report</button>
              <div className="modal fade" id="verticalycentered" tabIndex="-1">
                <div className="modal-dialog modal-dialog-centered">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">Reports to Download</h5>
                      <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                      <div className="row mb-3">
                        <div className="col-sm-10">
                          {Object.entries(REPORT_HASH).map(([key, value_n_name]) => {
                            return (<div className="form-check">
                              <input className="form-check-input" type="checkbox" id={key} name={key} defaultChecked={value_n_name[0]} onChange={this.handleCheckClick}/>
                              <label className="form-check-label" for="gridCheck1">{value_n_name[1]}</label>
                            </div> 
                          )})}
                        </div>
                      </div>

                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                      <button type="button" className="btn btn-primary" onClick={this.requestForReports}>Save changes</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <hr></hr>
        </div>

        <br></br>
        {noRecords ? null : 
        <div class="col-lg-12 dashboard">
          <div class="row">
            {Object.entries(this.state.reportSummary).map(([key, value]) => {
            return(
              <div class="col-xxl-4 col-md-6">
                <div class={"card info-card "+ ICON_HASH_FOR_SUMMERY[key][1]}>

                  <div class="filter">
                    <a class="icon" data-bs-toggle="dropdown"><i class="bi bi-three-dots"></i></a>
                    <ul class="dropdown-menu dropdown-menu-end dropdown-menu-arrow">
                      <li class="dropdown-header text-start"><h6>Options</h6></li>
                      <li><a class="dropdown-item" onClick={this.handleShowDrillDown}>View Stones</a></li>
                    </ul>
                  </div>

                  <div class="card-body">
                    <h5 class="card-title">{key} <span>| Inventory</span></h5>

                    <div class="d-flex align-items-center" style={{cursor: 'pointer'}} onClick={this.handleShowDrillDown} data-bs-toggle="tooltip" data-bs-placement="top" title="Click to see Stones">
                      <div class="card-icon rounded-circle d-flex align-items-center justify-content-center">
                        <i class={"bi " + ICON_HASH_FOR_SUMMERY[key][0]}></i>
                      </div>
                      <div class="ps-3">
                        <h6>{value}</h6>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )})}
          </div>
          <div className={"modal fade "+ drilldownClass} id="fullscreenModal" style={drilldownStyle}>
            <div className="modal-dialog modal-fullscreen">
              <div className="modal-content">
                <div className="modal-header">
                  <div className="pagetitle">
                    <h1>My Inventory</h1>
                  </div>
                  <button type="button" className="btn-close" onClick={this.handleHideDrillDown}></button>
                </div>
                <div className="modal-body">
                  <div className="row">
                    {Object.entries(this.state.reportSummary).map(([key, value]) => {
                    return(
                      <div className="col-xxl-4 col-md-6">
                        <div className={"card info-card "+ ICON_HASH_FOR_SUMMERY[key][1]}>
                          <div className="card-body">
                            <h5 className="card-title">{key} <span>| Inventory</span></h5>
                            <div className="d-flex align-items-center">
                              <div className="card-icon rounded-circle d-flex align-items-center justify-content-center">
                                <i className={"bi " + ICON_HASH_FOR_SUMMERY[key][0]}></i>
                              </div>
                              <div className="ps-3">
                                <h6>{value}</h6>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    )})}
                  </div>
                  <div className='drilldown'>
                    { drilldownClass ?  <AgingReport isCsr={this.props.isCsr} filterHash={this.state.filterHash}/> : null}
                  </div>
                </div>
              </div>
            </div>
          </div>
          <hr></hr>
        </div>}

        <br></br>
        {noRecords ? null : <div className="pagetitle text-center">
          <h1>BreakDown</h1>
          <br></br>
        </div>}
        {noRecords ? null : <StaticReport items={this.state.itemsData} DataisLoaded={this.state.BreakdownDataLoaded}/>}
      </div>
    )
  }
}
export default SaleReport
